public byte[] getSrcPathsHash(String[] srcs) {
  synchronized (digest) {
    for (String src : srcs) {
      digest.update(src.getBytes(Charsets.UTF_8));
    }
    byte[] result = digest.digest();
    digest.reset();
    return result;
  }
}
