      snapshotManager.resetSnapshottable(path);
    } finally {
      dir.writeUnlock();
    }
    getEditLog().logDisallowSnapshot(path);
  } finally {
    writeUnlock();
  }
  getEditLog().logSync();

  if (auditLog.isInfoEnabled() && isExternalInvocation()) {
    logAuditEvent(true, "disallowSnapshot", path, null, null);
  }
}
