public void computeNodeDensity() {
  float sum = 0;
  int volcount = 0;
  for (DiskBalancerVolumeSet vset : volumeSets.values()) {
    for (DiskBalancerVolume vol : vset.getVolumes()) {
      sum += Math.abs(vol.getVolumeDataDensity());
      volcount++;
    }
  }
  nodeDataDensity = sum;
  this.volumeCount = volcount;

}
