private void setPlanParams(List<NodePlan> plans) {
  for (NodePlan plan : plans) {
    for (Step step : plan.getVolumeSetPlans()) {
      if (this.bandwidth > 0) {
        step.setBandwidth(this.bandwidth);
      }
      if (this.maxError > 0) {
        step.setMaxDiskErrors(this.maxError);
      }
    }
  }
}
