protected boolean containsStorageDir(StorageLocation location, String bpid)
    throws IOException {
  for (StorageDirectory sd : storageDirs) {
    if (location.matchesStorageDirectory(sd, bpid)) {
      return true;
    }
  }
  return false;
}
